mapper.select:
SELECT p.classify_id, p.classify_id, p.brand_id, p.shop_id, p.pro_sn, p.name, p.variety, p.thickness, p.fineness, p.color, p.function, p.unit_price, p.price, p.company, p.number, p.whole_weight, p.origin, p.deposit, p.warehouse, p.sales_address, p.explaindesc, p.good_verify, p.quality_verify, p.quality_warranty, p.bargaining, p.extension_margin, p.financing_margin, p.supplier_delivery, p.compensation, p.video_inspection, p.warehouse_charge, p.subsidy, p.lightning_invoice, p.honest_shop, p.on_the_way, p.directional, p.bidding_price, p.spot_listing, p.new, p.supply_chain, p.guarantee_pay, p.size, p.length, p.break_length, p.break_elongation, p.regain, p.crimp_degree, p.double_length, p.whiteness, p.defect, p.oil, p.sulfur, p.burn, p.add_time, p.state, p.stock, p.sale_type, pa.data_id, pa.orders_sort, pa.suffix, pa.cn_name, pa.en_name, pa.file_path, pa.type, sv.classify_id, sv.pro_id, sv.spec_id, sv.sku_id, sv.spec_val, pk.classify_id, pk.pro_id, pk.sku_sn, pk.stock, pk.sale_price, pk.market_price, pk.cost_price, av.orders_sort, av.classify_id, av.pro_id, av.attribute_value FROM f_product p JOIN f_product_attach pa on p.id = pa.data_id JOIN f_specification_value sv on p.id = sv.pro_id JOIN f_product_sku pk on p.id = pk.pro_id JOIN f_attribute_value av on p.id = av.pro_id
AND p.state=#{productPageDto.state,jdbcType=INTEGER}
AND p.pro_sn LIKE concat('%',#{productPageDto.proSn,jdbcType=VARCHAR},'%')
